import WagonsPanel from "./WagonsPanel";
import SeastPanel from './SeatsPanel';

function BottomPanel({ currentTrain, handleWagonChange}) {
  
  const handleWagonClick = wagon_index =>{
    handleWagonChange(wagon_index)
  }

  return (
    <>
      <WagonsPanel currentTrain={currentTrain} focusOnWagonClick={handleWagonClick}/>
      <SeastPanel currentWagon={currentTrain.wagons[current_wagon_index]} />
    </>
  );
}
  
export default BottomPanel;
