import React, { useState } from 'react';
import CanvasPanel from "./Canvas/Panels/CanvasPanel"
import TrainsPanel from "./Canvas/Panels/TrainsPanel"
import BottomPanel from "./Canvas/Panels/BottomPanel"
import TrainManager from "./Canvas/Logic/TrainManager"
import './App.css'

function App() {
  const [trainManager] = useState(new TrainManager());
  const [currentTrain, setTrain] = useState(trainManager.getCurrentTrain())

  const handleTrainChange = newTrain =>{
    setTrain(newTrain)
  }

  return(
    <div className="app">
        <div className="left_panel">
          <CanvasPanel/>
        </div>
        <div className="right_panel">
          <TrainsPanel trainManager={trainManager} focusOnTrainClick={handleTrainChange}/>
        </div>
        <div className="bottom_panel">
          <BottomPanel currentTrain={currentTrain}/>
        </div>
    </div>
  );
}

export default App;
