import Train from "./Train"

class TrainManager
{
    constructor()
    {
        this.selectedTrainIndex = 0;
        this.trains = [
            new Train("WKD"), 
            new Train("SKM"), 
            new Train("ŁS"),
            new Train("IC")];
    }

    focusOnTrain(train)
    {
        const index = this.trains.findIndex(t => t === train);
        if (index !== -1) {
            this.selectedTrainIndex = index;
        } 
        console.log(this.getCurrentTrain())
    }

    getCurrentTrain()
    {
        return this.trains[this.selectedTrainIndex];
    }

}

export default TrainManager;
