class DrawUtils
{
    
    drawStation(ctx, posx, posy, name){
        ctx.beginPath();
        ctx.arc(posx, posy, 5, 0, 2 * Math.PI);
        ctx.fillStyle = '#A2C5AC';
        ctx.strokeStyle = '#454851';
        ctx.lineWidth = 2;
        ctx.fill();
        ctx.stroke();
        ctx.font = '12px Arial';
        ctx.fillStyle = 'black';
        ctx.textAlign = 'center';
        ctx.fillText(name, posx, posy - 10);
    }

    drawTrain(ctx, train){
        var posx = train.posX;
        var posy = train.posY;
        var name = train.name;
        var directionX = train.track.track.getCurrentStation().posX;
        var directionY = train.track.track.getCurrentStation().posY;
        
        // Calculate the angle between the current position and the direction
        const angle = Math.atan2(directionY - posy, directionX - posx);
      
        // Save the current transformation matrix
        ctx.save();
      
        // Translate the canvas origin to the position of the train
        ctx.translate(posx, posy);
      
        // Rotate the canvas context to align with the direction
        ctx.rotate(angle);
      
        // Draw the train
        // Train body
        ctx.beginPath();
        ctx.fillStyle = '#D3D5D4';
        ctx.fillRect(-15, -5, 30, 10);
        ctx.strokeStyle = '#454851';
        ctx.lineWidth = 2;
        ctx.strokeRect(-15, -5, 30, 10);
      
        // Train front
        ctx.beginPath();
        ctx.moveTo(-15, -5);
        ctx.lineTo(-25, 0);
        ctx.lineTo(-15, 5);
        ctx.closePath();
        ctx.fill();
        ctx.stroke();
      
        // Train rear
        ctx.beginPath();
        ctx.moveTo(15, -5);
        ctx.lineTo(25, 0);
        ctx.lineTo(15, 5);
        ctx.closePath();
        ctx.fill();
        ctx.stroke();
      
        // Train name
        ctx.font = '12px Arial';
        ctx.fillStyle = 'black';
        ctx.textAlign = 'center';
        ctx.fillText(name, 0, -15);
      
        // Restore the original transformation matrix
        ctx.restore();
    }

    drawLine(ctx, startX, startY, endX, endY){
        ctx.beginPath();
        ctx.moveTo(startX, startY);
        ctx.lineTo(endX, endY);
        ctx.strokeStyle = '#D3D5D4';
        ctx.lineWidth = 5;
        ctx.stroke();
    }
}

export default DrawUtils;
