import Locomotive from "./Locomotive";
import Train from "./Train"
import Wagon from "./Wagon";

class TrainManager
{
    constructor()
    {
        // Locomotives
        var IC9215 = new Locomotive("IC9215", "Poland", 312);
        var LI3122 = new Locomotive("LI3122", "Germany", 200);
        var KK1233 = new Locomotive("KK1233", "Sweden", 400);

        var wkd_wagons = [
            new Wagon("wkd2213", 1, 20),
            new Wagon("wkd2213", 1, 20),
            new Wagon("wkd2300", 1, 28),
            new Wagon("wkd2100", 2, 30),
            new Wagon("wkd2100", 2, 30),
            new Wagon("wkd300r", 3, 30)];
            
        var skm_wagons = [
            new Wagon("skm0r12", 1, 30),
            new Wagon("skm2r12", 2, 30)];
            
        var ls_wagons = [
            new Wagon("ls43321", 1, 30),
            new Wagon("ls43333", 2, 30),
            new Wagon("ls43333", 2, 30),
            new Wagon("ls43333", 2, 30)];
            
        var ic_wagons = [
            new Wagon("ic_fast", 1, 10),
            new Wagon("ic_fast", 1, 10),
            new Wagon("ic_fast", 1, 10),
            new Wagon("ic_fast", 1, 10),
            new Wagon("ic_fast", 1, 10),
            new Wagon("ic_fast", 1, 10),
            new Wagon("ic_fast", 1, 10),
            new Wagon("ic_fast", 1, 10),
            new Wagon("ic_fast", 1, 10)];

        // Trains
        var WKD = new Train("WKD", IC9215, wkd_wagons);


        this.selectedTrainIndex = 0;
        this.trains = [
            WKD];
    }

    focusOnTrain(train)
    {
        const index = this.trains.findIndex(t => t === train);
        if (index !== -1) {
            this.selectedTrainIndex = index;
        } 
        console.log(this.getCurrentTrain())
    }

    getCurrentTrain()
    {
        return this.trains[this.selectedTrainIndex];
    }

}

export default TrainManager;
