import Seat from "./Seat"

class Train {
    constructor(name)
    {
        this.name = name;
        this.seats = Array(40).fill(null);
        this.resetSeats();
    }

    resetSeats()
    {
        for(let i = 0; i < 40; i++)
        {
            this.seats[i] = new Seat(i+1, "", false)
        }
    }

    occupyRandomSeats(numSeatsToOccupy) {
        const seatIndices = Array.from({ length: 40 }, (_, index) => index);
        seatIndices.sort(() => Math.random() - 0.5);
        for (let i = 0; i < numSeatsToOccupy; i++) {
          const seatIndex = seatIndices[i];
          if (seatIndex !== undefined) {
            this.seats[seatIndex].occupy("Marek Mostowiak");
          }
        }
      }

}

export default Train;
