import React, { useEffect, useRef } from 'react';

function CanvasPanel({stationsManager, connectionsManager}) {

  const drawMap = (ctx) => {
    const img = new Image();
    img.src = process.env.PUBLIC_URL + 'mapa_polski.jpg';
    img.onload = () => {
      ctx.drawImage(img, 0, 0, 500, 500);
      drawStations(ctx);
      drawConnections(ctx);
    };
  }

  const drawPoint = (ctx, posx, posy, name) => {
    ctx.beginPath();
    ctx.arc(posx, posy, 5, 0, 2 * Math.PI);
    ctx.fillStyle = '#A2C5AC';
    ctx.fill();
    ctx.stroke();
    ctx.font = '12px Arial';
    ctx.fillStyle = 'black';
    ctx.textAlign = 'center';
    ctx.fillText(name, posx, posy - 10);
  };

  const drawLine = (ctx, startX, startY, endX, endY) => {
    ctx.beginPath();
    ctx.moveTo(startX, startY);
    ctx.lineTo(endX, endY);
    ctx.strokeStyle = 'blue';
    ctx.lineWidth = 20;
    ctx.stroke();
  };

  const drawStations = (ctx) => {
    stationsManager.stations.forEach(station => drawPoint(ctx, station.posX, station.posY, station.name));
  }

  const drawConnections = (ctx) => {
    connectionsManager.connections.forEach(connection => drawLine(ctx, connection.city1.x, connection.city1.y, connection.city2.x, connection.city2.y))
  }

  const canvasRef = useRef(null);
  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    ctx.clearRect(0,0,500,500);
    drawMap(ctx);
  });

  return <canvas ref={canvasRef} width={500} height={500}/>;
}

export default CanvasPanel;
