import SeatElement from "../Elements/SeatElement";
import React, { useState } from 'react';
import WagonsPanel from "./WagonsPanel";

function BottomPanel({ currentTrain }) {
  const [current_wagon_index, setWagon] = useState(0);
  
  const handleWagonChange = wagon_index =>{
    setWagon(wagon_index)
  }

  return (
    <>
      <WagonsPanel currentTrain={currentTrain} focusOnWagonClick={handleWagonChange}/>
      
      {currentTrain.wagons[0].seats.map((seat, index) => (
        <SeatElement key={index} Seat={seat}/>
      ))}
      <h1>{currentTrain.name}</h1>
    </>
  );
}
  
export default BottomPanel;
