import Locomotive from "./Locomotive";
import Train from "./Train"
import Wagon from "./Wagon";

class TrainManager
{
    constructor()
    {
        // Locomotives
        IC9215 = new Locomotive("IC9215", "Poland", 312);
        LI3122 = new Locomotive("LI3122", "Germany", 200);
        KK1233 = new Locomotive("KK1233", "Sweden", 400);

        // Create WKD
        wkd1 = new Wagon("wkd standard", 1, 30)
        wkd2 = new Wagon("wkd standard", 1, 30)
        wkd3 = new Wagon("wkd standard", 1, 30)
        wkd4 = new Wagon("wkd standard", 1, 30)
        wkd5 = new Wagon("wkd standard", 1, 30)

        WKD = new Train("WKD", IC9215, )


        this.selectedTrainIndex = 0;
        this.trains = [
            new Train("WKD"), 
            new Train("SKM"), 
            new Train("ŁS"),
            new Train("IC")];
    }

    focusOnTrain(train)
    {
        const index = this.trains.findIndex(t => t === train);
        if (index !== -1) {
            this.selectedTrainIndex = index;
        } 
        console.log(this.getCurrentTrain())
    }

    getCurrentTrain()
    {
        return this.trains[this.selectedTrainIndex];
    }

}

export default TrainManager;
