import React, { useEffect, useRef } from 'react';
import DrawUtils from './DrawUtils';

function CanvasPanel({stationsManager, connectionsManager, trainManager, current_train_index}) {
  const drawUtils = new DrawUtils();

  const drawMap = (ctx, img) => 
  {
    ctx.drawImage(img, 0, 0, 500, 500);
  }

  const drawStations = (ctx) => 
  {
    stationsManager.stations.forEach(station => drawUtils.drawStation(ctx, station.posX, station.posY, station.name));
  }

  const drawConnections = (ctx) => 
  {
    connectionsManager.connections.forEach(connection => drawUtils.drawLine(ctx, connection.city1.posX, connection.city1.posY, connection.city2.posX, connection.city2.posY))
  }

  const drawTrains = (ctx) =>
  {
    trainManager.trains.map((train, index) => drawUtils.drawTrain(ctx, train, index === current_train_index))
   
  }

  const canvasRef = useRef(null);
  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    const img = new Image();
    img.src = process.env.PUBLIC_URL + 'mapa_polski.jpg';
    img.onload = () => {
      drawMap(ctx, img);
      drawConnections(ctx);
      drawStations(ctx);
    };
    let animationInterval; // Variable to hold the interval ID

    // Function to start the animation loop
    const startAnimation = () => {
      // Animation variables
      let lastTimestamp = performance.now();
  
      // Function to update train positions
      function updateTrainPositions(timestamp) {
        // Calculate elapsed time since last frame
        const deltaTime = timestamp - lastTimestamp;
        lastTimestamp = timestamp;
        console.log("frame");
  
        // Clear the canvas
        ctx.clearRect(0, 0, canvas.width, canvas.height);
  
        // Redraw map, connections, and stations
        drawMap(ctx, img);
        drawConnections(ctx);
        drawStations(ctx);
  
        // Update and draw trains
        drawTrains(ctx);
  
        // Request the next frame
        requestAnimationFrame(updateTrainPositions);
      }
  
      // Start the animation loop
      animationInterval = setInterval(() => {
        requestAnimationFrame(updateTrainPositions);
      }, 10000); // Run the animation loop every second
    };
  
    // Function to stop the animation loop
    const stopAnimation = () => {
      clearInterval(animationInterval); // Clear the animation interval
    };
  
    // Start the animation when the component mounts
    startAnimation();
  
    // Stop the animation when the component unmounts or when dependencies change
    return () => {
      stopAnimation();
    };
  }, [stationsManager, connectionsManager, trainManager, current_train_index]);

  return <canvas ref={canvasRef} width={500} height={500} />;
}

export default CanvasPanel;
