class Train {
    constructor(name, locomotive, wagons, index)
    {
      this.name = name;
      this.locomotive = locomotive;
      this.wagons = wagons;
      this.index = index;
    }

    get_occupied_seats()
    {
      let occupied_seats = 0;
      this.wagons.forEach((wagon, index) => {
        wagon.seats.forEach((seat, index) => {
          occupied_seats += seat.isOccupied ? 1 : 0;
        });
      });
      return occupied_seats;
    }

    get_all_seats()
    {
      let seats = 0;
      this.wagons.forEach((wagon, index) => {
        seats += wagon.seats.length;
      });
      return seats;
    }
}

export default Train;
