class DrawUtils
{
    
    drawStation(ctx, posx, posy, name){
        ctx.beginPath();
        ctx.arc(posx, posy, 5, 0, 2 * Math.PI);
        ctx.fillStyle = '#A2C5AC';
        ctx.strokeStyle = '#454851';
        ctx.lineWidth = 2;
        ctx.fill();
        ctx.stroke();
        ctx.font = '12px Arial';
        ctx.fillStyle = 'black';
        ctx.textAlign = 'center';
        ctx.fillText(name, posx, posy - 10);
    };    

    drawTrain(ctx, posx, posy, name){
        // Train body
        ctx.beginPath();
        ctx.fillStyle = '#D3D5D4';
        ctx.fillRect(posx - 15, posy - 5, 30, 10);
        ctx.strokeStyle = '#454851';
        ctx.lineWidth = 2;
        ctx.strokeRect(posx - 15, posy - 5, 30, 10);
      
        // Train front
        ctx.beginPath();
        ctx.moveTo(posx - 15, posy - 5);
        ctx.lineTo(posx - 25, posy);
        ctx.lineTo(posx - 15, posy + 5);
        ctx.closePath();
        ctx.fill();
        ctx.stroke();
      
        // Train rear
        ctx.beginPath();
        ctx.moveTo(posx + 15, posy - 5);
        ctx.lineTo(posx + 25, posy);
        ctx.lineTo(posx + 15, posy + 5);
        ctx.closePath();
        ctx.fill();
        ctx.stroke();
      
        // Train name
        ctx.font = '12px Arial';
        ctx.fillStyle = 'black';
        ctx.textAlign = 'center';
        ctx.fillText(name, posx, posy);
    };

    drawLine(ctx, startX, startY, endX, endY){
        ctx.beginPath();
        ctx.moveTo(startX, startY);
        ctx.lineTo(endX, endY);
        ctx.strokeStyle = '#D3D5D4';
        ctx.lineWidth = 5;
        ctx.stroke();
    };
}

export default DrawUtils;
