function WagonsPanel({ currentTrain, focusOnWagonClick }) {
    
    const handleWagonClick = (train_index) =>
    {
        trainManager.focusOnTrain(train_index)
        focusOnWagonClick(train_index)
    };

    return (
        <>
        <h2>Active Trains</h2>
        {trainManager.trains.map((train, index) =>(
            <button className="btn_train" key={index} onClick={() => handleWagonClick(index)}>
            {train.name}
            </button>
        ))}
        </>
    );
  }
    
  export default WagonsPanel;
